import { act } from 'react-dom/test-utils'
import { USER_REQUEST, USER_DATA, USER_FAILURE } from './ActionType'

const initialState = {
    loading: false,
    users: [],
    error: ''
}



const reducer = (state = initialState, action) => {
    switch (action.type) {
        case USER_REQUEST:
            return {
                ...state,
                loading: true
            }
        case USER_DATA:
            return {
                ...state,
                loading: false,
                users: action.payload,
                error: ''
            }
        case USER_FAILURE:
            return {
                ...state,
                loading: false,
                users: [],
                error: action.payload
            }
        default: return false
    }
}

export default reducer
