import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { fetchingData, fetchingUserDetails } from '../redux/ReducerComponents/User/Action'
import './Main.css'

function Main({ userData, fetchingData, fetchingUserDetails }) {



    useEffect(() => {
        fetchingData()
    }, [])

    const readData = (id) => {
        fetchingUserDetails(id)
    }

    return (
        <div className="container">

            <div className="innerContainer">

                {
                    userData?.loading == true ? <h1>Loading...</h1> : userData?.error ? <h1>{userData?.error}</h1> :
                        userData?.users?.length > 1 ? userData?.users?.map((user) => {
                            return (
                                <div className="tableRow" key={user?.id}>
                                    <div className="tableContent">
                                        <p>{user?.name}</p>
                                    </div>
                                    <div className="tableActions">
                                        <div className="readButton">
                                            <p onClick={() => readData(user?.id)}>Read</p>
                                        </div>
                                        <div className="editButton">
                                            <p>Edit</p>
                                        </div>
                                        <div className="deleteButton">
                                            <p>Delete</p>
                                        </div>
                                    </div>
                                </div>
                            )
                        }) : (
                                userData?.users?.map((user) => {
                                    return (
                                        <div className="tableRow" key={user?.id}>
                                            <div className="tableContent">
                                                <p>{user?.name}</p>
                                            </div>

                                        </div>
                                    )
                                })
                            )
                }


            </div>


        </div>
    )
}


const mapStateToProps = state => {
    console.log(state.user)
    return {
        userData: state?.user
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchingData: () => dispatch(fetchingData()),
        fetchingUserDetails: (id) => dispatch(fetchingUserDetails(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
