import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { fetchingData, fetchingUserDetails } from '../redux/ReducerComponents/User/Action'
import './Main.css'
import { Formik } from 'formik'
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';

function Main({ userData, fetchingData, fetchingUserDetails, addData }) {

    const [name, setName] = useState('')
    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [phone, setPhone] = useState('')
    const [address, setAddress] = useState('')
    const [zipcode, setZipcode] = useState('')
    const [id, setId] = useState('')
    const [open, setOpen] = useState(false);

    const onOpenModal = () => setOpen(true);
    const onCloseModal = () => setOpen(false);

    useEffect(() => {
        const callData = () => {
            fetchingData()
        }
        callData()
    }, [])

    const [showUser, setShowuser] = useState(null)
    const readData = (id, user) => {
        // fetchingUserDetails(id)
        setShowuser(user)
    }

    const [visibleList, setVisiblelist] = useState({});

    const handleDelete = (id) => {
        if (visibleList[id] === undefined) {
            setVisiblelist({ ...visibleList, [id]: true })
        }
        else {
            // console.log("in else")
        }
    }

    const editData = (user) => {
        setName(user?.name)
        setUsername(user?.username)
        setEmail(user?.email)
        setPhone(user?.phone)
        setAddress(`${user?.address?.street} ${user?.address?.city}`)
        setZipcode(user?.address?.zipcode)
        setId(user?.id)
        onOpenModal()
    }

    return (
        <div className="container">

            <div className="innerContainer">
                <Link to="/add-user" className="addButton">
                    <p>Add Button</p>
                </Link>

                {
                    userData?.loading == true ? <h1>Loading...</h1> : userData?.error ? <h1>{userData?.error}</h1> :
                        (
                            showUser === null || showUser === undefined) ? (
                                userData?.users?.concat(userData?.newData)?.reverse()?.map((user) => {
                                    if (visibleList[user?.id] === undefined)
                                        return (
                                            <div className="tableRow" key={user?.id}>
                                                <div className="tableContent">
                                                    <p>{user?.name}</p>
                                                </div>
                                                <div className="tableActions">
                                                    <div className="readButton">
                                                        <p onClick={() => readData(user?.id, user)}>Read</p>
                                                    </div>
                                                    <div className="editButton">
                                                        <p onClick={() => editData(user)}>Edit</p>
                                                    </div>
                                                    <div className="deleteButton">
                                                        <p onClick={() => handleDelete(user?.id)}>Delete</p>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    else {
                                        return (
                                            <>
                                            </>
                                        )
                                    }
                                })
                            ) :
                            (
                                <div className="particularData">
                                    <div className="block">
                                        <div className="blockTitle">
                                            <p>Name</p>
                                        </div>
                                        <div className="blockDetail">
                                            <p>{showUser?.name}</p>
                                        </div>
                                    </div>
                                    <div className="block">
                                        <div className="blockTitle">
                                            <p>Username</p>
                                        </div>
                                        <div className="blockDetail">
                                            <p>{showUser?.username}</p>
                                        </div>
                                    </div>
                                    <div className="block">
                                        <div className="blockTitle">
                                            <p>Email</p>
                                        </div>
                                        <div className="blockDetail">
                                            <p>{showUser?.email}</p>
                                        </div>
                                    </div>
                                    <div className="block">
                                        <div className="blockTitle">
                                            <p>Phone</p>
                                        </div>
                                        <div className="blockDetail">
                                            <p>{showUser?.phone}</p>
                                        </div>
                                    </div>
                                    <div className="block">
                                        <div className="blockTitle">
                                            <p>Address</p>
                                        </div>
                                        <div className="blockDetail">
                                            {
                                                showUser?.address?.street ? <p>{showUser?.address?.street}, {showUser?.address?.city}</p> : <p>{showUser?.address}</p>
                                            }

                                        </div>
                                    </div>
                                    <div className="block">
                                        <div className="blockTitle">
                                            <p>Zip Code</p>
                                        </div>
                                        <div className="blockDetail">
                                            {
                                                showUser?.address?.zipcode ? <p>{showUser?.address?.zipcode}</p> : <p>{showUser?.zipcode}</p>
                                            }

                                        </div>
                                    </div>


                                    <div className="BackButton">
                                        <p onClick={() => setShowuser(null)}>Back</p>
                                    </div>
                                </div>

                            )

                }


            </div>

            <Modal closeOnOverlayClick={false} open={open} onClose={onCloseModal} classNames={{
                overlay: 'customOverlay',
                modal: 'customModal',
            }} center>
                <div className="formContainer">

                    <div className="innerFormContainer">
                        <h1>Anywhere in your app!</h1>
                        <Formik
                            initialValues={{ email: email, name: name, username: username, address: { street: address, zipcode: zipcode }, phone: phone, }}
                            validate={values => {
                                const errors = {};
                                var num = /^\d{6}/
                                var regx = /^[6-9]\d{9}$/;
                                if (!values.email) {
                                    errors.email = 'Required Email';
                                } else if (
                                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                                ) {
                                    errors.email = 'Invalid email address';
                                }

                                if (!values.name) {
                                    errors.name = "Required Name";
                                }
                                if (!values.username) {
                                    errors.username = "Required Username"
                                }
                                if (!values.address.street) {
                                    errors.address = "Required address"
                                }
                                if (!values.phone) {
                                    errors.phone = "Required phone"
                                }
                                // else if (!regx.test(values.phone)) {

                                //     errors.phone = "Check your number"
                                // }
                                if (!values.address.zipcode) {
                                    errors.zipcode = "Required zipcode"
                                }
                                // else if (!num.test(values.address.zipcode)) {

                                //     errors.zipcode = "Check your Zipcode"
                                // }
                                return errors;
                            }}
                            onSubmit={(values, { setSubmitting, resetForm }, event) => {
                                // event.preventDefault()

                                values.id = id
                                alert(JSON.stringify(values))
                                resetForm()
                                setSubmitting(false)
                                // alert("Successfully Saved")
                            }}
                        >
                            {({
                                values,
                                errors,
                                touched,
                                handleChange,
                                handleBlur,
                                handleSubmit,
                                isSubmitting,
                                /* and other goodies */
                            }) => (
                                <form onSubmit={handleSubmit}>
                                    <div className="topInputs">

                                        <div className="nameInput">
                                            <p>Name</p>
                                            <input
                                                type="name"
                                                name="name"
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                value={values.name}
                                            />
                                            <h6>{errors.name && touched.name && errors.name}</h6>
                                        </div>
                                        <div className="emailInput">
                                            <p>Email</p>
                                            <input
                                                type="email"
                                                name="email"
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                value={values.email}
                                            />
                                            <h6>{errors.email && touched.email && errors.email}</h6>
                                        </div>
                                    </div>

                                    <div className="topInputs">

                                        <div className="nameInput">
                                            <p>Username</p>
                                            <input
                                                type="username"
                                                name="username"
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                value={values.username}
                                            />
                                            <h6>{errors.username && touched.username && errors.username}</h6>
                                        </div>
                                        <div className="emailInput">
                                            <p>Address</p>
                                            <input
                                                type="address"
                                                name="address"
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                value={values.address.street}
                                            />
                                            <h6>{errors.address && touched.address && errors.address}</h6>
                                        </div>
                                    </div>


                                    <div className="topInputs">

                                        <div className="nameInput">
                                            <p>Phone</p>
                                            <input
                                                type="phone"
                                                name="phone"
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                value={values.phone}
                                            />
                                            <h6>{errors.phone && touched.phone && errors.phone}</h6>
                                        </div>
                                        <div className="emailInput">
                                            <p>Zip Code</p>
                                            <input
                                                type="zipcode"
                                                name="zipcode"
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                value={values.address.zipcode}
                                            />
                                            <h6>{errors.zipcode && touched.zipcode && errors.zipcode}</h6>
                                        </div>
                                    </div>
                                    <button type="submit" disabled={isSubmitting}>
                                        Submit
                                    </button>
                                </form>
                            )}
                        </Formik>
                    </div>
                </div>
            </Modal>

        </div>
    )
}


const mapStateToProps = state => {
    return {
        userData: state?.user,
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchingData: () => dispatch(fetchingData()),
        // fetchingUserDetails: (id) => dispatch(fetchingUserDetails(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
