import { act } from 'react-dom/test-utils'
import { USER_REQUEST, USER_DATA, USER_FAILURE, ADD_DATA } from './ActionType'

const initialState = {
    loading: false,
    users: [],
    error: '',
    newData: []
}



export default function reducer(state = initialState, action) {
    console.log("allState", state)
    switch (action.type) {
        case USER_REQUEST:
            return {
                ...state,
                loading: true
            }
        case USER_DATA:
            return {
                ...state,
                users: action.payload,
            }
        case USER_FAILURE:
            return {
                ...state,
                error: action.payload
            }
        case ADD_DATA:
            console.log("Action", action.payload)
            var newArr = state.newData.concat(action.payload)
            return {
                ...state,
                newData: newArr,
            }
        default: return "zaid"
    }
}
