import { act } from 'react-dom/test-utils'
import { USER_REQUEST, USER_DATA, USER_FAILURE, ADD_DATA } from './ActionType'

const initialState = {
    loading: false,
    users: [],
    error: '',
    newData: []
}



const reducer = (state = initialState, action) => {
    switch (action.type) {
        case USER_REQUEST:
            return {
                ...state,
                loading: true
            }
        case USER_DATA:
            return {
                ...state,
                loading: false,
                users: action.payload,
            }
        case USER_FAILURE:
            return {
                ...state,
                error: action.payload
            }
        case ADD_DATA:
            console.log("Action", action.payload)
            var newArr = state.newData.concat(action.payload);
            return {
                ...state,
                loading: false,
                newData: newArr,
            }
        default: return false
    }
}

export default reducer
