import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { fetchingData, fetchingUserDetails } from '../redux/ReducerComponents/User/Action'
import './Main.css'

function Main({ userData, fetchingData, fetchingUserDetails }) {

    useEffect(async () => {
        await fetchingData()
    }, [])

    const [showUser, setShowuser] = useState(null)
    const readData = (id, user) => {
        // fetchingUserDetails(id)
        setShowuser(user)
    }

    const [visibleList, setVisiblelist] = useState({});

    const handleDelete = (id) => {

        console.log(visibleList[id])
        if (visibleList[id] === undefined) {
            setVisiblelist({ ...visibleList, [id]: true })
        }
        else {
            console.log("in else")
        }
    }

    return (
        <div className="container">

            <div className="innerContainer">

                {
                    userData?.loading == true ? <h1>Loading...</h1> : userData?.error ? <h1>{userData?.error}</h1> :
                        showUser ? (
                            userData?.users?.map((user) => {
                                if (visibleList[user?.id] === undefined)
                                    return (
                                        <div className="tableRow" key={user?.id}>
                                            <div className="tableContent">
                                                <p>{user?.name}</p>
                                            </div>
                                            <div className="tableActions">
                                                <div className="readButton">
                                                    <p onClick={() => readData(user?.id, user)}>Read</p>
                                                </div>
                                                <div className="editButton">
                                                    <p>Edit</p>
                                                </div>
                                                <div className="deleteButton">
                                                    <p onClick={() => handleDelete(user?.id)}>Delete</p>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                else {
                                    return (
                                        <>
                                        </>
                                    )
                                }
                            })
                        ) : (
                                userData?.users?.map((value) => {
                                    return (
                                        <div className="particularData">
                                            <div className="block">
                                                <div className="blockTitle">
                                                    <p>Name</p>
                                                </div>
                                                <div className="blockDetail">
                                                    <p>{value?.name}</p>
                                                </div>
                                            </div>
                                            <div className="block">
                                                <div className="blockTitle">
                                                    <p>Username</p>
                                                </div>
                                                <div className="blockDetail">
                                                    <p>{value?.username}</p>
                                                </div>
                                            </div>
                                            <div className="block">
                                                <div className="blockTitle">
                                                    <p>Email</p>
                                                </div>
                                                <div className="blockDetail">
                                                    <p>{value?.email}</p>
                                                </div>
                                            </div>
                                            <div className="block">
                                                <div className="blockTitle">
                                                    <p>Phone</p>
                                                </div>
                                                <div className="blockDetail">
                                                    <p>{value?.phone}</p>
                                                </div>
                                            </div>
                                            <div className="block">
                                                <div className="blockTitle">
                                                    <p>Address</p>
                                                </div>
                                                <div className="blockDetail">
                                                    <p>{value?.address?.street}, {value?.address?.city}</p>
                                                </div>
                                            </div>
                                            <div className="block">
                                                <div className="blockTitle">
                                                    <p>Zip Code</p>
                                                </div>
                                                <div className="blockDetail">
                                                    <p>{value?.address?.zipcode}</p>
                                                </div>
                                            </div>


                                            <div className="BackButton">
                                                <p onClick={fetchingData}>Back</p>
                                            </div>
                                        </div>
                                    )
                                })
                            )
                }


            </div>


        </div>
    )
}


const mapStateToProps = state => {
    console.log("mapStateToProps", state.user)
    return {
        userData: state?.user
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchingData: () => dispatch(fetchingData()),
        fetchingUserDetails: (id) => dispatch(fetchingUserDetails(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
